#include <iostream>
#include <string>
#include <algorithm>
#include <limits>
using std::cin;
using std::cout;
using std::endl;
using std::getline;
using std::max;
using std::min;
using std::sort;

int main()
{
	short tcases = 0;
    cin >> tcases;

	while (tcases--)
	{
		int m, n;

		getline(cin, m);
		getline(cin, n);
		if (m > n) { m = n; }
		if (n > m) {n = m; }

		const int MAXN = max(std::max(m, n), 1);

    std::vector<std::string> games(MAXN);
    int i = 0; // A
    // Check whether the game is on board game or not
    std::sort(games.begin() + i, games.end(),
              [](const std::string & s1, const std::string & s2) {
                    int d2 = max(s1.size(), s2.size()); // d2 - difference
                    if (d2 < (int)games.size()) // not on board game or not
                    {
                        return (int)(s1.size() - games.size()); // take s1
                    }
                    else
                        return max(s1.size(), s2.size());
              });

    for (; i < games.size(); ++i)
    {
      if (games[i].find("Monopoly") != std::string::npos) { i++; break; }
      if (games[i].find("Scrabble") != std::string::npos) { i++; break; }
      if (games[i].find("Chess") != std::string::npos) { i++; break; }
      if (games[i].find("Checkers") != std::string::npos) { i++; break; }
    }

		sort(games.begin() + n - m + 1, games.end());

    std::cerr << (n - m + 1 - i) << " " << (n - m + 1) << "\n";
    for (int j = (n - m + 1) - i - 2; j >= 0; j--)
        cout << games[j];
    cout << "\n";
	}
	return 0;
}

