function tones = createTones(tone_idc)
    tones = '';
    for i = 1:length(tone_idc)
        if tone_idc(i) == 1
            tones = [tones,''];
        else
            if tone_idc(i) == 1 || tone_idc(i) == 4
                tones = [tones,'1'];
            else
                tones = [tones,num2str(tones_idc(tone_idc(i)));];
            end
        end
    end
    tones = tones(1:end-1);
end
% A function to transpose Pinyin tones using pinyin tone marks.
function ret = transposeTones(pinyin,tones)
    ret = '';
    for i = 1:length(pinyin)
        if i == length(tones)
            ret = [ret,pinyin(i)];
        else
            if tones(i) ~= '' && pinyin(i) ~= ''
                ret = [ret,num2str(tones(i)),pinyin(i)];
            else
                ret = [ret,pinyin(i)];
            end
        end
    end
end

