public static boolean isPalindrome(String str) {
    // Remove non-alphanumeric characters and convert to lowercase
    String cleanedString = str.replaceAll("[^A-Za-z0-9]", "").toLowerCase();

    // Initialize two pointers at the start and end of the string
    int left = 0;
    int right = cleanedString.length() - 1;

    // Compare characters from both ends moving towards the center
    while (left < right) {
        if (cleanedString.charAt(left) != cleanedString.charAt(right)) {
            // If characters don't match, it's not a palindrome
            return false;
        }
        // Move pointers towards the center
        left++;
        right--;
    }

    // If no mismatches were found, it's a palindrome
    return true;
}

public static void main(String[] args) {
    String example = "racecar";
    System.out.println("Is \"" + example + "\" a palindrome? " + isPalindrome(example)); // true
}

