using namespace std;

void quicksort(int nums[], int low, int high) {
    if (low < high) {
        int pivot_index = partition(nums, low, high);
        quicksort(nums, low, pivot_index - 1);
        quicksort(nums, pivot_index + 1, high);
    }
}

int partition(int nums[], int low, int high) {
    int pivot = nums[low];
    int i = low - 1;
    int j = high + 1;

    while (true) {
        do {
            i++;
        } while (nums[i] < pivot);

        do {
            j--;
        } while (nums[j] > pivot);

        if (i >= j) {
            return j;
        }

        swap(nums[i], nums[j]);
    }
