public int[] productExceptSelfSol1(int[] nums) {   
int[] answer = new int[nums.length];   
if (nums == null || nums.length < 1) {   
return answer;   
}   
int temp = 1;   
for (int i = 0; i < nums.length; ++i) {   
answer[i] = temp;   
temp *= nums[i];   
}   
temp = 1;   
for (int i = nums.length - 1; i >= 0; --i) {   
answer[i] = answer[i] * temp;   
temp *= nums[i];   
}   
return answer;   
}   
