public int[] productExceptSelfTwoLoop(int[] nums) {   
int[] ret = new int[nums.length];   
int[] fromLeft = new int[nums.length];   
int[] fromRight = new int[nums.length];   
for (int i = 0; i < nums.length; i++) {   
if (i == 0) {   
fromLeft[i] = nums[0];   
} else {   
fromLeft[i] = fromLeft[i-1] * nums[i];   
}   
}   
for (int i = nums.length-1; i >= 0; i--) {   
if (i == nums.length - 1) {   
fromRight[i] = nums[nums.length-1];   
} else {   
fromRight[i] = fromRight[i+1] * nums[i];   
}   
}   
for (int i = 0; i < nums.length; i++) {   
int left = (i == 0) ? 1 : fromLeft[i-1];   
int right = (i == nums.length-1) ? 1 : fromRight[i+1];   
ret[i] = left * right;   
}   
return ret;   
}   
