#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    vector<vector<int>> diagonalSort(vector<vector<int>>& mat) {
        int r = mat.size(), c = mat[0].size();

        // sort diagonals from lower right to upper left
        for (int row = r - 1; row >= 0; row--){
            vector<int> temp;
            // collect diagonal elements into a list
            for (int i = row, j = 0; i < r && j < c; i++, j++)
                temp.push_back(mat[i][j]);
            // sort the list
            sort(temp.begin(), temp.end());
            // put sorted elements into the matrix
            for (int i = row, j = 0, it = 0; i < r && j < c; i++, j++, it++)
                mat[i][j] = temp[it];
        }

        // sort diagonals from lower left to upper right
        for (int col = 1; col < c; col++){
            vector<int> temp;
            // collect diagonal elements into a list
            for (int i = 0, j = col; i < r && j < c; i++, j++)
                temp.push_back(mat[i][j]);
            // sort the list
            sort(temp.begin(), temp.end());
            // put sorted elements into the matrix
            for (int i = 0, j = col, it = 0; i < r && j < c; i++, j++, it++)
                mat[i][j] = temp[it];
        }
        return mat;
    }
};

