A: I'll recommend you to install the ExcelDataReader nuget. This library is able to read excel files from any format and even from an excel that is open.
    public static IEnumerable<TModel> GetExcelData<TModel>(Stream excelFile) where TModel : class, new()
    {
        var models = new List<TModel>();
        using (var reader = ExcelReaderFactory.CreateReader(excelFile))
        {
            while (reader.Read())
            {
                var model = new TModel();
                var data = reader.GetString(0);
                try
                {
                    model = ConvertExcelData(model, data);
                }
                catch
                {
                    model = null;
                }

                if (model != null)
                {
                    models.Add(model);
                }

                yield return model;
            }
        }
    }


    public static TModel ConvertExcelData<TModel>(TModel model, string data) where TModel : class, new()
    {
        var members = typeof(TModel).GetProperties();
        var dataSplitted = data.Split(';');

        for (int i = 0; i < members.Count(); i++)
        {   
            (string propertyName, var propertyType) = (members[i].Name, members[i].PropertyType);
            var value = GetValue(dataSplitted[i], propertyType);

            if (value == null && !SkipUndefinedValues)
                continue;

            members[i].SetValue(model, value);
        }

        return model;
    }   

    private static object GetValue(string value, Type type)
    {
        if (value == null)
        {
            value = "{}";
        }

        if (type == typeof(string))
        {
            return value;
        }
        else if (type == typeof(int))
        {
            return Convert.ToInt32(value);
        }
        else if (type == typeof(double))
        {
            return Convert.ToDouble(value);
        }
        else if (type == typeof(decimal))
        {
            return Convert.ToDecimal(value);
        }

        return value;
    }

It will return a IEnumerable with the models that it was able to read from the excel table;
